import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Sidebar from './components/Sidebar'; 
import Profile from './pages/Profile';
import Login from './pages/Login';
import Signup from './pages/Signup';
import LocatorPage from './pages/LocatorPage';
import SymptomCheckerPage from './pages/SymptomCheckerPage';
import Settings from './pages/Settings';
import './assets/styles/main.css';

const App = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchedUser = localStorage.getItem('user');
    if (fetchedUser) {
      setUser(JSON.parse(fetchedUser)); // Set user state if found in local storage
    }
  }, []);

  const signOut = () => {
    localStorage.removeItem('user'); // Clear user from local storage
    setUser(null); // Reset user state
    alert('You have been signed out.');
  };

  const ProtectedRoute = ({ element }) => {
    return user ? element : <Navigate to="/login" />;
  };

  return (
    <Router>
      <div className="app-container">
        {/* Pass user and signOut to Sidebar */}
        <Sidebar user={user} logout={signOut} />

        {/* Main Content */}
        <div className="main-content">
          <Routes>
            <Route path="/" element={<Profile />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/locator" element={<LocatorPage />} />
            <Route path="/symptoms" element={<SymptomCheckerPage />} />
            <Route path="/settings" element={<ProtectedRoute element={<Settings user={user} />} />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
