// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import Profile from './pages/Profile';
import Login from './pages/Login';
import Signup from './pages/Signup';
import LocatorPage from './pages/LocatorPage';
import SymptomCheckerPage from './pages/SymptomChecker';
import Settings from './pages/Settings';
import Consultation from './pages/Consultation';
import NearHospital from './pages/NearHospital';
import './assets/styles/main.css';

const App = () => {
  const [user, setUser] = useState(null);

  // Check if the user is in localStorage and set it to state
  useEffect(() => {
    const fetchedUser = localStorage.getItem('user');
    if (fetchedUser) {
      setUser(JSON.parse(fetchedUser)); // Parse the user data if it exists in localStorage
    }
  }, []);

  const signOut = () => {
    localStorage.removeItem('user'); // Remove the user from localStorage on sign out
    setUser(null); // Reset the user state
    alert('You have been signed out.');
  };

  const ProtectedRoute = ({ element }) => {
    return user ? element : <Navigate to="/login" />; // If no user, redirect to login page
  };

  return (
    <Router>
      <div className="app-container">
        <Sidebar user={user} logout={signOut} /> {/* Pass user and signOut to Sidebar */}

        <div className="main-content">
          <Routes>
            {/* Public Routes */}
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />

            {/* Protected Routes */}
            <Route path="/" element={<ProtectedRoute element={<Dashboard />} />} />
            <Route path="/profile" element={<ProtectedRoute element={<Profile user={user} />} />} />
            <Route path="/locator" element={<ProtectedRoute element={<NearHospital />} />} />
            <Route path="/symptoms" element={<ProtectedRoute element={<SymptomCheckerPage />} />} />
            <Route path="/consultation" element={<ProtectedRoute element={<Consultation />} />} />
            <Route path="/settings" element={<ProtectedRoute element={<Settings user={user} />} />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
